import React from 'react';
import {Link} from 'react-router-dom';
import { Card } from 'react-bootstrap';
import styled from 'styled-components';
import {
        Col,
        Row,
        } from 'react-bootstrap';

const Kard = styled(Card)`
  margin-bottom:25px; 
  border-radius: 15px;
  padding: 30px 20px;
`;

const CardTitle = styled.h1`
  font-size: 15px;
  text-transform: uppercase;
  margin-bottom: 1rem;
  min-height: 45px;
  font-weight: bold;
  font-family: "Futura-bold";
  color: #626262;
`;

const Text = styled.p`
  margin-right: 0 !important;
  font-weight: bold;
  line-height: 20px;
  font-size: 15px;
  font-family: 'futuramedium';
  color: #9F9F9F;
`;

const MainTitle = styled(CardTitle)`
    font-size: 32px;
    color: #000;
    text-transform: capitalize;
`;

function Articlelist({articles, column, name}) {
  return (
      <Row xs={1} md={column} className="pt-3 pb-5">
       <Col md={12}><MainTitle>{name}</MainTitle></Col>
        {
          articles.map((article, key) => (
              <Col key={key} >
                <Link to={`/article/${article.name}`} className="article-list-item">
                    <Kard>
                      <Card.Body className="p-0">
                        <CardTitle>{article.title}</CardTitle>
                        <img src={require(article.banner)} alt="safaricom"/>
                        <Text>
                        {article.content[0].substring(0 , 150)}.... read more
                        </Text>
                      </Card.Body>
                  </Kard>
                </Link>
              </Col>
          ))
        }
      </Row>
  )
}

export default Articlelist;
