import React from 'react';
import { Row,
         Col,        
} from 'react-bootstrap';

import Bannerimage from '../images/banner-one.jpg';

function Banner() {
  return (
    <>
    <Row xs={1} md={2} className="align-items-center">
        <Col md={5}>
            <h1>Banner Here</h1>
        </Col>
        <Col md={7}>
            <img src={Bannerimage} alt="safaricom" width="100%"/>
        </Col>
    </Row>
    </>
  )
}

export default Banner;
